#!/usr/bin/env python3

# Custom topology for Mininet, generated by gentopo.py.

from mininet.topo import Topo
from mininet.net import Mininet
from mininet.node import RemoteController
from mininet.node import Node
from mininet.node import OVSSwitch
from mininet.link import TCLink
from mininet.cli import CLI
from mininet.log import setLogLevel
from mininet.util import dumpNodeConnections
from functools import partial
from collections import defaultdict, OrderedDict
import utils.ipv4
class Mynet(Topo):
    def build(self):
        switches = {}
        def add(l: int, subnet: str, *names: str):
            for i, name in enumerate(names):
                host = self.addHost(name, ip=utils.ipv4.get_nth_host(subnet, i))
                switch = self.addSwitch("s{}".format(i + l + 1))
                switches[name] = switch
                self.addLink(host, switch)

        def link(s1: str, s2: str, delay: int):
            self.addLink(switches[s1], switches[s2], bw=10, delay="{}ms".format(delay))

        add(0, "10.12.0.0/16", "arizona", "asu", "berkeley", "ucla1", "colorado", "oregon", "osu", "usc1", "stanford", "utah", "washington", "wsu")
        add(12, "10.10.0.0/17", "illinois", "iowa", "minnesota", "nebraska", "northwest", "purdue", "wisconsin", "ucla2", "usc2")
        add(21, "10.10.128.0/17", "indiana", "maryland", "michigan", "msu", "ohio", "psu", "rutgers")

        link("washington", "wsu", 2)
        link("washington", "osu", 5)
        link("osu", "oregon", 1)
        link("wsu", "oregon", 6)
        link("oregon", "berkeley", 7)
        link("berkeley", "stanford", 1)
        link("berkeley", "utah", 17)
        link("stanford", "ucla1", 6)
        link("ucla1", "usc1", 1)
        link("usc1", "asu", 9)
        link("asu", "arizona", 1)
        link("utah", "colorado", 11)
        link("ucla1", "ucla2", 0)
        link("usc1", "usc2", 0)
        link("minnesota", "iowa", 14)
        link("minnesota", "wisconsin", 11)
        link("iowa", "illinois", 8)
        link("illinois", "northwest", 2)
        link("illinois", "nebraska", 10)
        link("illinois", "ucla2", 42)
        link("illinois", "purdue", 11)
        link("northwest", "wisconsin", 3)
        link("wisconsin", "usc2", 46)
        link("purdue", "indiana", 2)
        link("indiana", "ohio", 9)
        link("ohio", "michigan", 6)
        link("ohio", "maryland", 17)
